<?php class GuestsController extends Controller {			public function homepage()	{				$this->set('header', 'Homepage');	}	public function login()	{		if (!empty($_POST['username']) && !empty($_POST['password']))		{									$user = $this->_model->select_user_from_login($_POST);			if ( sizeof($user) > 0)			{				$_SESSION['userrole'] = $user[0]['Login']['userrole'];								switch ($user[0]['Login']['userrole'])				{					case "administrator":						$sendToPage = BASE_URL."admins/homepage";					break;					case "root":						$sendToPage = BASE_URL."roots/homepage";					case "customer":						$sendToPage = BASE_URL."guest/homepage";					break;				}				$header = "U bent succesvol ingelogd";			}			else			{				$header = "Wachtwoord en/of gebruikersnaam niet juist<br />						   U wordt doorgestuurd naar de homepage";				$sendToPage = BASE_URL."guest/homepage";			}		}		else		{			$header = "U heeft een van de velden niet ingevuld.<br />					   U wordt doorgestuurd naar de					   Homepage";			$sendToPage = BASE_URL."guest/homepage";		}		$this->set("header", $header);		header("refresh:1;url=".$sendToPage);	}	public function register()	{		$this->_model->insert_register_data($_POST);		header("location:../guests/homepage");	}	 }?>